<template>
	<div class="content">
    <div class="author">
        <div class="author name" v-for="item in musicDetailsName" :key="item.id">
            {{item.name}}
        </div>
    </div>
    <div class="banner-img">
      <img :src="musicDetailsImg" alt="" style="width:550rpx;height:550rpx" :class="{'xuanzhuan': xuanzhuan}">
    </div>
    <swiper class="musicComment" :autoplay='true' :interval='3000' :circular='true' :vertical='true' :display-multiple-items='3'>
      <block v-for="item in musicComment" :key="item.id">
        <swiper-item class="musicCommentItem">
          <img :src="item.user.avatarUrl" alt=""> ：{{item.content}}
        </swiper-item>
      </block>
    </swiper>
    <div class="musicMini">
      <i :style="dianzan.zhuangtai?dianzan.zhen:dianzan.jia" @click="dianzanClick"></i>
      <i style="background:url(data:image/svg+xml;base64,); background-size:56rpx 56rpx; width:56rpx;height:56rpx;"></i>
      <i style="background:url(data:image/svg+xml;base64,); background-size:56rpx 56rpx; width:56rpx;height:56rpx;"></i>
      <i style="background:url(data:image/svg+xml;base64,); background-size:56rpx 56rpx; width:56rpx;height:56rpx;"></i>
    </div>
    <div class="musicIcon">
      <i style="background:url(data:image/svg+xml;base64,); background-size:56rpx 56rpx; width:56rpx;height:56rpx;"></i>
        <i @click="chooseSong('prev')" style="background:url(data:image/svg+xml;base64,); background-size:76rpx 76rpx; width:76rpx;height:76rpx;"></i>
        <i @click="listenerButtonPause" v-bind:style="[zanting.zhuangtai ? zanting.zanting : zanting.kaishi, errorClass]"></i>
        <i @click="chooseSong('next')" style="background:url(data:image/svg+xml;base64,); background-size:76rpx 76rpx; width:76rpx;height:76rpx;"></i>
        <i @click="playing" style="background:url(data:image/svg+xml;base64,); background-size:56rpx 56rpx; width:56rpx;height:56rpx;"></i>
    </div>
  </div>
</template>
<script>
import { mapState, mapMutations, mapActions } from 'vuex';
import { getStorage } from '@/utils/wechat';
import { CHANGE_CURR_MUSIC } from '@/store/mutations-type';
let backgroundAudioManager = wx.getBackgroundAudioManager();
export default {
	data(){
		return {
			xuanzhuan: true,
			musicUrlData: "",
			musicDetailsImg: "",
      musicDetailsName: "",
      musicDetailsData: [],
      musicComment: [],
    	index: 0,
    	isPlay: true,
			zanting: {
	      zhuangtai: true,
	      zanting:
	          "background:url(data:image/svg+xml;base64,);background-size:120rpx 120rpx; width:120rpx;height:120rpx;",
	      kaishi:
	          "background:url(data:image/svg+xml;base64,);background-size:120rpx 120rpx; width:120rpx;height:120rpx;"
      },
      dianzan: {
        zhuangtai: false,
        zhen:
            "background:url(data:image/svg+xml;base64,); background-size:56rpx 56rpx; width:56rpx;height:56rpx;",
        jia:
            "background:url(data:image/svg+xml;base64,); background-size:56rpx 56rpx; width:56rpx;height:56rpx;"
      }
		}
	},
	computed: {
    ...mapState('songList', {
      currMusic: state => state.currMusic
    })
  },
	async onLoad(options){
		await this.asyncChangeCurrMusic({id: options.id, isPlay: true})
		this.musicDetailsImg = this.currMusic.picUrl;
		this.musicUrlData = this.currMusic.url;
		this.musicDetailsName = this.currMusic.singer;
		this.musicComment = this.currMusic.commit;
		backgroundAudioManager.src = this.musicUrlData;
		backgroundAudioManager.title = this.currMusic.title;
		backgroundAudioManager.singer = this.musicDetailsName;
		backgroundAudioManager.coverImgUrl = this.musicDetailsImg;
	},
	mounted(){
		wx.getBackgroundAudioManager().onStop(()=>{
	    const url = "../index/main";
	    wx.navigateTo({ url });
  	})
  	wx.getBackgroundAudioManager().onEnded(()=>{
  		this.chooseSong('next');
	  })
	},
	methods: {
		...mapMutations('songList', {
			'changCurrMusic': CHANGE_CURR_MUSIC
		}),
		...mapActions('songList', ['asyncChangeCurrMusic']),
		// 选择歌曲
	  async chooseSong(type){
	  	let playList;
	  	await getStorage('playList').then((json) => {
				playList = JSON.parse(json.data);
			})
	    switch (type) {
	      case 'prev':
	        if (this.index <= 0) {
	          this.index = playList.length - 1;
	        } else {
	          this.index -= 1;
	        }
	        break;
	      case 'next':
	        if (this.index >= playList.length - 1) {
	          this.index = 0;
	        } else {
	          this.index += 1;
	        }
	        break;
	      default:
	      	break;
	    }
	    const id = playList[this.index].mainTrackId || playList[this.index].id;
	    await this.asyncChangeCurrMusic({id: id},{isPlay: true})
	    this.musicDetailsImg = this.currMusic.picUrl;
			this.musicUrlData = this.currMusic.url;
			this.musicDetailsName = this.currMusic.singer;
			this.musicComment = this.currMusic.commit;
			backgroundAudioManager.src = this.musicUrlData;
			backgroundAudioManager.title = this.currMusic.title;
			backgroundAudioManager.singer = this.musicDetailsName;
			backgroundAudioManager.coverImgUrl = this.musicDetailsImg;
	  },
	  //监听button暂停按钮
    listenerButtonPause: function() {
      if (this.zanting.zhuangtai) {
        wx.pauseBackgroundAudio({});
        this.zanting.zhuangtai = false;
      } else {
        wx.playBackgroundAudio({});
        this.zanting.zhuangtai = true;
      }
      if (this.xuanzhuan) {
        this.xuanzhuan = false;
      } else {
        this.xuanzhuan = true;
      }
      this.changCurrMusic({isPlay: this.zanting.zhuangtai})
    },
	  playing() {
      wx.switchTab({
       	url: "../playlist/main" //注意switchTab只能跳转到带有tab的页面，不能跳转到不带tab的页面
      });
	  },
	  dianzanClick(){
	  	this.dianzan.zhuangtai = !this.dianzan.zhuangtai;
	  }
	}
}
</script>
