  animation: rotate 60s linear infinite;
}
.musicMini {
  display: flex;
  justify-content: space-between;
  align-items: center;
  position: fixed;
  bottom: 200rpx;
  width: 670rpx;
}
.musicIcon {
  position: fixed;
  bottom: 30rpx;
  width: 670rpx;
  display: flex;
  justify-content: space-between;
  align-items: center;
}
.musicComment {
  margin-left: 40rpx;
  margin-top: 40rpx;
  width: 630rpx;
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  height: 220rpx;
  overflow: hidden;
}
.musicCommentItem {
  font-size: 24rpx;
  line-height: 60rpx;
  position: relative;
  padding-left: 70rpx;
  color: #666;
}
